// frontend/src/app/components/dashboard/dashboard.component.ts
import { Component, OnInit, OnDestroy } from '@angular/core';
import { WebsocketService, SensorData } from '../../services/websocket.service';

@Component({
  selector: 'app-dashboard',
  templateUrl: './dashboard.component.html',
  styleUrls: ['./dashboard.component.css']
})
export class DashboardComponent implements OnInit, OnDestroy {
  sensors: SensorData[] = [];
  connectionStatus: string = 'Disconnected';
  private ws: WebSocket | null = null;

  // üëá Angular INYECTA el servicio autom√°ticamente
  constructor(private websocketService: WebsocketService) {}

  ngOnInit() {
    // üëá Usamos el servicio en lugar de tener la l√≥gica aqu√≠
    this.ws = this.websocketService.connect((data: SensorData) => {
      this.sensors.unshift(data);
      if (this.sensors.length > 10) this.sensors.pop();
      this.connectionStatus = 'Connected ‚úÖ';
    });
  }

  ngOnDestroy() {
    if (this.ws) {
      this.ws.close();
    }
  }
}
